    private void contentMenuItemActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_contentMenuItemActionPerformed
        // TODO add your handling code here:
        Reserva form = new Reserva();
        Escritorio.add(form);
        form.toFront();
        form.setVisible(true);
        Reserva.txtidTrabajador.setText(lblidPersona.getText());
        Reserva.txtTrabajador.setText(lblNombre.getText() + " " + lblApellido_Paterno.getText());
        Reserva.idUsuario = Integer.parseInt(lblidPersona.getText());
    }//GEN-LAST:event_contentMenuItemActionPerformed

